var axios = require('axios');

var jsonp = require('jsonp');

module.exports = {
  getPBPVideoURL: function getPBPVideoURL(vid) {
    return new Promise(function (resolve, reject) {
      var url = 'https://stats.nba.com/stats/videoeventsasset?GameEventID=' + vid.EventNum + '&GameID=' + vid.GameID;

      if (typeof window === "undefined") {
        axios.get(url).then(function (res) {
          var vidUrl = res.data.resultSets.Meta.videoUrls[0].lurl;
          resolve(vidUrl);
        }).catch(function (err) {
          console.log(err);
          reject(err);
        });
      } else {
        jsonp(url, null, function (err, res) {
          var vidUrl = res.resultSets.Meta.videoUrls[0].lurl;
          resolve(vidUrl);
        }).catch(function (err) {
          console.log(err);
          reject(err);
        });
      }
    });
  }
};
